mapper.insert:

  SELECT NVL(MAX(seq) + 1, 1) AS seq
    FROM BOARD

INSERT INTO BOARD 
            (seq
            ,title
            ,content
            ,display
            ,group_id
            ,group_order
            ,depth
            ,delete_yn
            ,reg_date
            ,reg_id
            ,upd_date
            ,upd_id) 
     VALUES (#{seq}
            ,#{title}
            ,#{content}
            ,#{display}
            ,#{seq}
            ,#{groupOrder}
            ,#{depth}
            ,#{deleteYn}
            ,#{regDate}
            ,#{regId}
            ,#{updDate}
            ,#{updId})

mapper.update:
UPDATE BOARD
   SET title = #{title}
      ,content = #{content}
      ,display = #{display}
      ,upd_date = #{updDate}
      ,upd_id = #{updId}
 WHERE seq = #{seq}
   AND delete_yn = false

UPDATE BOARD 
   SET group_order = group_order+1  
 WHERE group_id= #{groupId}
   AND group_order > #{groupOrder}

UPDATE BOARD
   SET delete_yn = true
 WHERE seq = ${seq}
