mapper.insert:

  SELECT NVL(MAX(seq) + 1, 1) AS seq
    FROM BOARD

INSERT INTO BOARD 
            (seq
            ,title
            ,content
            ,display
            ,group_id
            ,group_order
            ,depth
            ,delete_yn
            ,reg_date
            ,reg_id
            ,upd_date
            ,upd_id) 
     VALUES (#{seq}
            ,#{title}
            ,#{content}
            ,#{display}
            ,#{seq}
            ,#{groupOrder}
            ,#{depth}
            ,#{deleteYn}
            ,#{regDate}
            ,#{regId}
            ,#{updDate}
            ,#{updId})
